
$(".nav__item").hover(onIn, onOut);
$(".nav__show").click(function() {
    if ((window.innerWidth <= 901)) {
        $(this).parents(".nav__item").toggleClass("nav__item--active")
        $(this).siblings(".nav__hidden").slideToggle()
    }
})


function onIn() {
    if (window.innerWidth > 901) {
        let el = $(this)
        setTimeout(function() {
            if (el.is(':hover')) {
                el.addClass("nav__item--active")
            }
        }, 200);
    }
}

function onOut() {
    if (window.innerWidth > 901) {
        $(this).removeClass("nav__item--active")
    }
}


$(".burger").click(function () {
    $(".header").toggleClass("_open")
})

function mainSlider() {
    var swiper = new Swiper('.main .swiper-container', {
        slidesPerView: 1,
        spaceBetween: 0,
        pagination: {
            el: '.main .swiper-pagination',
            type: 'bullets',
            clickable: true,
            renderBullet: function (index, className) {
                index++
                return '<span class="' + className + '">' + index + '</span>';
            },
        },
    })
}

